// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract ProductTraceability {
    struct Step {
        string description;
        string date;
        address actor;
    }
    
    mapping(uint => Step[]) private productHistory;
    mapping(address => bool) public authorizedActors;
    address public owner;
    uint public productCount;

    event StepAdded(uint productId, string description, string date, address actor);
    event ActorAuthorized(address actor);

    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner can perform this action");
        _;
    }

    modifier onlyAuthorized() {
        require(authorizedActors[msg.sender], "Not authorized");
        _;
    }

    constructor() {
        owner = msg.sender;
    }

    function authorizeActor(address _actor) external onlyOwner {
        authorizedActors[_actor] = true;
        emit ActorAuthorized(_actor);
    }

    function addStep(uint _productId, string memory _description, string memory _date) external onlyAuthorized {
        if(_productId == 0) {
            productCount++;
            _productId = productCount;
        }
        
        productHistory[_productId].push(Step({
            description: _description,
            date: _date,
            actor: msg.sender
        }));
        
        emit StepAdded(_productId, _description, _date, msg.sender);
    }

    function getHistory(uint _productId) external view returns (Step[] memory) {
        return productHistory[_productId];
    }
}
